function formatarData(dataStr) {
  if (!dataStr || typeof dataStr !== "string") return dataStr;

  // já está no formato YYYY-MM-DD, só retornar
  if (dataStr.includes("-")) {
    return dataStr;
  }

  // se vier no formato DD/MM/YYYY, converte
  if (dataStr.includes("/")) {
    const [dia, mes, ano] = dataStr.split("/");
    return `${ano}-${mes.padStart(2, "0")}-${dia.padStart(2, "0")}`;
  }

  // formato desconhecido — devolve como veio
  return dataStr;
}



function getLaunchTags(campaignName, checklist) {
  if (!checklist || checklist.length === 0) return []

  const nomeCampanha = campaignName.toLowerCase()
  let tagEncontrada

  for (let rawTag of checklist) {
    if (!rawTag) continue

    // garante que é string
    // rawTag = String(rawTag).replace(/\[|\]/g, "").trim()

    if (rawTag && nomeCampanha.includes(rawTag.toLowerCase())) {
      tagEncontrada = rawTag
    }
  }

  return tagEncontrada
}
