let bufferObj = Buffer.from(request, "base64");
let decodedString = bufferObj.toString("utf8");
let json = JSON.parse(decodedString);

if (json.body?.data?.createdAt?.includes("/")) {
  json.body.data.createdAt = json.body.data.createdAt.replace(
    /(\d{2})\/(\d{2})\/(\d{4})/,
    "$3-$2-$1"
  );
}

return json;

