function isValidObject(obj) {
    return obj && typeof obj === 'object' && Object.keys(obj).length > 0;
}

function mapJsonToTransaction(data, stored_date, queue_id) {
    if (!isValidObject(data)) return null;

    return {
          order_date: data.dates.ordered_at ? data.dates.ordered_at : data.dates.ordered_at || data.dates.ordered_at,
          update_at: data.dates.updated_at || null,
          approved_date: data.payment?.processing_times?.finished_at || data.dates.confirmed_at || null,
          canceled_time: data.dates.canceled_at || null,
          warrantly_expire_date: data.dates?.warranty_until || null,
          ext_id: data.id || null,
          // invoice_code: data.transaction || null,
          // invoice_cycle: Number.isNaN(parseInt(data.recurrency)) ? null : parseInt(data.recurrency),
          // invoice_discount: data.invoice?.discount_value || null,
          // invoice_id: data.transaction || null,
          // invoice_increment_value: data.invoice?.increment_value || null,
          // invoice_payment_url: (data.transaction?.qrcode ?? data.sale?.boleto_url) || (data.currentSale?.qrcode ?? data.currentSale?.boleto_url) || null,
          // invoice_subscription_id: data.transaction || null,
          invoice_status: data.status || null,
          invoice_next_cycle: Number.isNaN(parseInt(data.recurrency)) ? null : parseInt(data.recurrency) + 1,
          invoice_type: "TRANSACTION",
          subscriber_id: data.contact?.id || null,
          subscriber_internal_id: data.contact?.id || null,
          subscriber_name: data.contact?.name || null,
          subscriber_email: data.contact?.email || null,
          subscriber_phone: data.contact?.phone_number || null,
          subscriber_doc: data.contact?.doc || null,
          subscriber_status: data.contact?.subscription_status || null,
          contact_address: data.contact?.address || null,
          contact_address_city: data.contact?.address_city || null,
          contact_address_comp: data.contact?.address_comp || null,
          contact_address_country: data.contact?.address_country || null,
          contact_address_district: data.contact?.address_district || null,
          contact_address_number: data.contact?.address_number || null,
          contact_address_state: data.contact?.address_state || null,
          contact_address_full_name: data.contact?.client?.street && data.contact?.client?.number ? `${data.contact?.client.street}, ${data.contact?.client.number}` : null,
          contact_address_zip_code: data.contact?.address_zip_code || null,
          product_id: data.product?.prod || null,
          product_marketplace_id: data.product?.id || null,
          product_name: data.product?.name || null,
          product_offer_id: data.product?.offer?.id || null,
          product_offer_name: data.product?.offer?.name || null,
          payment_method: data.payment?.method || null,
          payment_price: data.payment?.total || null,
          offer_price: data.product?.total_value || null,

          utm_source: data.source?.utm_source || null,
          utm_medium: data.source?.utm_medium || null,
          utm_campaign: data.source?.utm_campaign || null,
          utm_term: data.source?.utm_term || null,
          utm_content: data.source?.utm_content || null,
          src: data.source?.source || null,
          xcod: data.source?.xcod || null,

          commision_value: data.payment?.net || 0,
          sck: data.sck || null,
          installments_number: data.payment?.installments?.qty || 0,
          installments_value: data.payment?.installments?.value || 0,
          commission_plataform: data.payment?.marketplace_value || 0,
          commission_affiliate: data.payment?.affiliate_value || 0,

          affiliate_name: (() => {
            const affs = data.affiliations || [];
            if (!Array.isArray(affs) || affs.length === 0) return null;

            const ids = affs
              .flatMap(a => [a.name])
              .filter(Boolean);

            if (ids.length === 0) return null;
            return ids.join(', ');
          })() || null,

          affiliate_code: (() => {
            const affs = data.affiliations || [];
            if (!Array.isArray(affs) || affs.length === 0) return null;

            const ids = affs
              .flatMap(a => [a.marketplace_id])
              .filter(Boolean);

            if (ids.length === 0) return null;
            return ids.join(', ');
          })() || null,

          platform: "guru",
          product_value: data.product?.unit_value || 0,
          refusal_reason: data.payment?.refuse_reason || data.payment?.refund_reason || null,
          currency: data.payment?.currency || null,
          stored_date,
          queue_id,      
          queue_table: "Guru_Vendas"
    };
}


function mapJsonToProductWithOffer(data) {
    if (!isValidObject(data.product || $input.first().json.product)) return null;

    return {
        product_marketplace_id: data.product?.id || data.product?.slug || null, // Corrigido
        product_name: data.product?.name || undefined,
        product_period: data.product?.period || 0,
        product_description: data.product?.name || undefined,
        product_price: data.product?.amount ? data.product.amount : undefined,
        product_class: "INFOPRODUCT",
        product_value: data.product?.unit_value || 0,
        product_code: data.product?.slug || undefined,
        platform: "guru",
        offer_ext: data.product?.offer?.id || null,
        offer_name: data.offer?.name || undefined,
        offer_price: data.product?.total_value || null
    };
}

const transactions = [];
const products = new Map();

for (const item of $input.all()) {
    if (!isValidObject(item?.json?.body || item?.json)) {
        console.warn("Item ignorado por não conter 'json.data':", item);
        continue;
    }

    const data = item.json.body || item.json;
    const stored_date =  item.json.stored_date;
    const queue_id = item.json.id;

    const transactionData = mapJsonToTransaction(data, stored_date, queue_id);
    if (transactionData) transactions.push(transactionData);

    const productData = mapJsonToProductWithOffer(data);
    if (productData && productData.product_marketplace_id) {
        products.set(productData.product_marketplace_id, productData);
    }
}

return [{
    insert: {
        transactions,
        products: Array.from(products.values())
    }
}];

