let datas = []
for (item in $node.Buffer.json) {
  if(item.includes('date')){
    datas.push($node.Buffer.json[item])
  }
}
function DataMaisAntiga(datas) {
    if (datas.length === 0) {
        return null;
    }

    const dataMaisAntiga = datas.reduce(function (dataAtual, data) {
        return data < dataAtual ? data : dataAtual;
    });

    return dataMaisAntiga;
}

function FormatDate(date){
  let dataUTC = new Date(date);

  dataUTC.setUTCHours(dataUTC.getUTCHours() - 3);

  return dataUTC.toISOString();
}

// (FormatDate(DataMaisAntiga(datas)))
return {data:FormatDate(DataMaisAntiga(datas))}

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

function isValidObject(obj) {
    return obj && typeof obj === 'object' && Object.keys(obj).length > 0;
}

function mapJsonToTransaction(data, stored_date, queue_id) {
    if (!isValidObject(data)) return null;

    return {
          id_transaction: data.transaction || null,
          update_at: data.purchase_date || null,
          ext_id: data.transaction || null,
          invoice_code: data.transaction || null,
          invoice_cycle: Number.isNaN(parseInt(data.recurrency)) ? null : parseInt(data.recurrency),
          invoice_discount: data.invoice?.discount_value || null,
          invoice_id: data.transaction || null,
          invoice_increment_value: data.invoice?.increment_value || null,
          invoice_payment_url: (data.transaction?.qrcode ?? data.sale?.boleto_url) || (data.currentSale?.qrcode ?? data.currentSale?.boleto_url) || null,
          invoice_subscription_id: data.transaction || null,
          invoice_status: data.status || null,
          invoice_next_cycle: Number.isNaN(parseInt(data.recurrency)) ? null : parseInt(data.recurrency) + 1,
          invoice_type: "TRANSACTION",
          subscriber_id: data.subscriber_code || null,
          subscriber_internal_id: data.client?.id || null,
          subscriber_name: data.name || null,
          subscriber_email: data.email || null,
          subscriber_phone: data.phone_number || null,
          subscriber_doc: data.doc || null,
          subscriber_status: data.subscription_status || null,
          contact_address: data.address || null,
          contact_address_city: data.address_city || null,
          contact_address_comp: data.address_comp || null,
          contact_address_country: data.address_country || null,
          contact_address_district: data.address_district || null,
          contact_address_number: data.address_number || null,
          contact_address_state: data.address_state || null,
          contact_address_full_name: data.client?.street && data.client?.number ? `${data.client.street}, ${data.client.number}` : null,
          contact_address_zip_code: data.address_zip_code || null,
          product_id: data.prod || null,
          product_marketplace_id: data.prod || null,
          product_name: data.prod_name || null,
          product_offer_id: data.off || null,
          product_offer_name: data.prod_name || null,
          payment_method: data.payment_type || null,
          payment_price: data.full_price || null,
          offer_price: data.price || null,

          utm_source: data.sck.split('|')[0] || null,
          utm_medium: data.sck.split('|')[1] || null,
          utm_campaign: data.sck.split('|')[2] || null,
          utm_term: data.sck.split('|')[3] || null,
          utm_content: data.sck.split('|')[4] || null,
          src: data.saleMetas?.find(meta => meta.meta_key === "src")?.meta_value || null,
          xcod: data.saleMetas?.find(meta => meta.meta_key === "xcod")?.meta_value || null,

          commision_value: data.cms_vendor || 0,
          sck: data.sck || null,
          installments_number: data.installments_number || null,
          commission_plataform: data.cms_marketplace || 0,
          commission_affiliate: data.cms_aff || 0,
          affiliate_name: data.aff_name || null,
          affiliate_code: data.aff || null,
          platform: "hotmart",
          product_value: data.price || null,
          currency: data.currency || null,
          stored_date,
          queue_id,      
          queue_table: "Hormart_Vendas"
    };
}


function mapJsonToProductWithOffer(data) {
    if (!isValidObject(data.product || $input.first().json.product)) return null;

    return {
        product_marketplace_id: data.product?.id || data.product?.slug || null, // Corrigido
        product_name: data.prod_name || undefined,
        product_period: data.product.period || 0,
        product_description: data.product?.description || undefined,
        product_price: data.product?.amount ? data.product.amount : undefined,
        product_class: "INFOPRODUCT",
        product_code: data.product?.slug || undefined,
        platform: "greenn",
        offer_ext: data.offer?.hash || null,
        offer_name: data.offer?.name || undefined,
        offer_price: data.offer?.amount ? data.offer.amount : undefined
    };
}

const transactions = [];
const products = new Map();

for (const item of $input.all()) {
    if (!isValidObject(item?.json?.body || item?.json)) {
        console.warn("Item ignorado por nÃ£o conter 'json.data':", item);
        continue;
    }

    const data = item.json.body || item.json;
    const stored_date =  item.json.stored_date;
    const queue_id = item.json.id;

    const transactionData = mapJsonToTransaction(data, stored_date, queue_id);
    if (transactionData) transactions.push(transactionData);

    const productData = mapJsonToProductWithOffer(data);
    if (productData && productData.product_marketplace_id) {
        products.set(productData.product_marketplace_id, productData);
    }
}

return [{
    insert: {
        transactions,
        products: Array.from(products.values())
    }
}];

