const database = $node['Aggregate'].json.data;
const sheet = $node['Aggregate1'].json.data;

const dbMap = {};
for (const row of database) {
  if (row.ext_id) {
    dbMap[row.ext_id] = row;
  }
}

const result = [];

for (const row of sheet) {
  const match = dbMap[row.ext_id];
  if (match) {
    result.push({
      row_number: row.row_number,
      ext_id: row.ext_id,
      order_date: match.order_date || null
    });
  }
}

return result.length ? result : [{}];
